mapper.select:
SELECT
    code, audioPath, deviceId, type
FROM
    sys_code
WHERE
    1 = 1
    AND deviceId = #{deviceId}
    AND sessionId = #{sessionId}
    AND code = #{code}
    AND createTime >= #{createTime}
    AND createTime >= DATE_SUB(NOW(),INTERVAL 10 MINUTE)
    ORDER BY createTime DESC
LIMIT 1

mapper.update:
UPDATE
    sys_code
SET
    audioPath = #{audioPath}
WHERE
    deviceId = #{deviceId}
    AND sessionId = #{sessionId}
    AND code = #{code}

UPDATE sys_device
state = #{state}, deviceName = #{deviceName}, wifiName = #{wifiName}, chipModelName = #{chipModelName}, type = #{type}, version = #{version}, ip = #{ip}, lastLogin = NOW(), roleId = #{roleId}, location = #{location},
WHERE 1 = 1 AND userId = #{userId} AND deviceId = #{deviceId}
