mapper.insert:
INSERT INTO sys_permission (
    parentId, name, permissionKey, permissionType, path, component, icon, sort, visible, status
) VALUES (
    #{parentId}, #{name}, #{permissionKey}, #{permissionType}, #{path}, #{component}, #{icon}, #{sort}, #{visible}, #{status}
)

mapper.update:
UPDATE sys_permission
parentId = #{parentId}, name = #{name}, permissionKey = #{permissionKey}, permissionType = #{permissionType}, path = #{path}, component = #{component}, icon = #{icon}, sort = #{sort}, visible = #{visible}, status = #{status},
WHERE permissionId = #{permissionId}
